package JavaPractice01.Thread;class MyThread extends Thread{    public void run(){        for(int i = 0; i<10; i++){            System.out.print(i + " ");            try{                Thread.sleep(500);            } catch (InterruptedException e){                e.printStackTrace();            }        }    }}public class ThreadTest01 {    public static void main(String[] args) {        Thread th = new MyThread();        th.start();        for(int i = 11; i<20; i++){            System.out.print(i + " ");        }    }}